export default {
    state: {
        // 应用的数据
        test: 'hello vuex',
        counters: [],
        counterTotal:0

    },
    getters: {
        counterSum: function (state) {
            let sum = 0;
            state.counters.forEach(element => {
                sum = sum + element.value;
            });
            return sum;
        }
    },
    mutations: {
        minusValue(state, index) {
            state.counters[index].value--;
            state.counterTotal--;
        },
        plusValue(state, index) {
            state.counters[index].value++;
            state.counterTotal++;
        },
        initCounters(state,countNumber){
            for (let index = 0; index < countNumber; index++) {
            state.counters.push({value:0});
            }
            }
    }
}
